mapper.select:
SELECT CPIMAGE, CPNAME, CPNUMBER, HANAME, THNAME, MAADDRESS
FROM CAMPINGLIST
WHERE CPCACODE = #{cpCaCode} AND CPMACFCODE = #{cpMaCfCode}

SELECT ROWNUMBER, CPMAMMCODE, CPMACFCODE, CPCACODE, CPCODE, CPHACODE, CPTHCODE, CPMAPOST, CPNAME, CPNUMBER, CPCONTENTS, CPFACILITY, CFNAME, HANAME, THNAME, MAADDRESS, CPIMAGE FROM(SELECT ROWNUM AS ROWNUMBER, CPMAMMCODE, CPMACFCODE, CPCACODE, CPCODE, CPHACODE, CPTHCODE, CPMAPOST, CPNAME, CPNUMBER, CPCONTENTS, CPFACILITY, CFNAME, HANAME, THNAME, MAADDRESS, CPIMAGE
FROM CAMPINGLIST WHERE ROWNUM <= #{pageNumber} * #{amount} AND CPCACODE = #{cpCaCode} AND CPMACFCODE = #{cpMaCfCode})
WHERE ROWNUMBER > (#{pageNumber}-1) * #{amount}

SELECT COUNT(*)
FROM CAMPINGLIST
WHERE CPCACODE = #{cpCaCode} AND CPMACFCODE = #{cpMaCfCode}

mapper.update:
UPDATE FP SET FP_TITLE = #{fpTitle}, FP_CONTENTS =#{fpContents} WHERE
FP_MACFCODE = #{fpMaCfCode} AND FP_CACODE = #{fpCaCode} AND FP_CODE =
#{fpCode}
