mapper.select:
SELECT ROWNUMBER, SRCFCODE, SRCODE, SRNAME, SRNUMBER, SRDETAILS, SRIMAGE, SRADDRESS FROM (SELECT ROWNUMBER, SRCFCODE, SRCODE, SRNAME, SRNUMBER, SRDETAILS, SRIMAGE, SRADDRESS
FROM (SELECT ROWNUM AS ROWNUMBER, SR_CFCODE AS SRCFCODE, SR_CODE AS SRCODE, SR_NAME AS SRNAME, SR_NUMBER AS SRNUMBER, SR_DETAILS AS SRDETAILS, SR_IMAGE AS SRIMAGE, SR_ADDRESS AS SRADDRESS FROM NATECHILDREN.STORES WHERE SR_CFCODE = #{srCfCode}) WHERE ROWNUMBER <= #{pageNumber} * #{amount} )
WHERE ROWNUMBER > (#{pageNumber}-1) * #{amount}

SELECT ROWNUMBER, CFCODE, CACODE, SRCODE, SRNAME, GOCODE, GONAME, DISCOUNT, RENTPRICE, IMAGE, STCODE FROM (SELECT ROWNUMBER ,CFCODE, CACODE, SRCODE, SRNAME, GOCODE, GONAME, DISCOUNT, RENTPRICE, IMAGE, STCODE
FROM (SELECT ROWNUM AS ROWNUMBER ,CFCODE, CACODE, SRCODE, SRNAME, GOCODE, GONAME, DISCOUNT, RENTPRICE, IMAGE, STCODE FROM GOODSLIST WHERE CFCODE = #{cfCode} AND CACODE = #{caCode}) WHERE ROWNUMBER <= #{pageNumber} * #{amount} )
WHERE ROWNUMBER > (#{pageNumber}-1) * #{amount}

SELECT ROWNUMBER, CFCODE, CACODE, SRCODE, SRNAME, GOCODE, GONAME, DISCOUNT, RENTPRICE, IMAGE, STCODE FROM (SELECT ROWNUMBER ,CFCODE, CACODE, SRCODE, SRNAME, GOCODE, GONAME, DISCOUNT, RENTPRICE, IMAGE, STCODE
FROM (SELECT ROWNUM AS ROWNUMBER ,CFCODE, CACODE, SRCODE, SRNAME, GOCODE, GONAME, DISCOUNT, RENTPRICE, IMAGE, STCODE FROM GOODSLIST WHERE CFCODE = #{cfCode} AND CACODE = #{caCode} AND SRCODE = #{srCode}) WHERE ROWNUMBER <= #{pageNumber} * #{amount} )
WHERE ROWNUMBER > (#{pageNumber}-1) * #{amount}

SELECT ROWNUMBER, CFCODE, CACODE, SRCODE, SRNAME, GOCODE, GONAME, DISCOUNT, RENTPRICE, IMAGE, STCODE FROM (SELECT ROWNUMBER ,CFCODE, CACODE, SRCODE, SRNAME, GOCODE, GONAME, DISCOUNT, RENTPRICE, IMAGE, STCODE
FROM (SELECT ROWNUM AS ROWNUMBER ,CFCODE, CACODE, SRCODE, SRNAME, GOCODE, GONAME, DISCOUNT, RENTPRICE, IMAGE, STCODE FROM GOODSLIST WHERE CFCODE = #{cfCode} AND CACODE = #{caCode} AND SRCODE != #{srCode}) WHERE ROWNUMBER <= #{pageNumber} * #{amount} )
WHERE ROWNUMBER > (#{pageNumber}-1) * #{amount}

SELECT COUNT(*)
FROM GOODSLIST
WHERE SRCODE=#{srCode} AND GOCODE = #{goCode}
